import React from "react";
import ReactDOM from "react-dom";
import { Provider } from "react-redux";
import thunk from "redux-thunk";
import "./index.css";
import App from "./App";
import * as serviceWorker from "./serviceWorker";
import MuiThemeProvider from "@material-ui/core/styles/MuiThemeProvider";
import { RootTheme } from "./styles";
import { createStore, applyMiddleware } from "redux";
import { func } from "prop-types";

const store = createStore(getProjectsReducer, applyMiddleware(thunk));

ReactDOM.render(
  <Provider store={store}>
    <App />
  </Provider>,
  document.getElementById("root")
);

const user = (state, action) => {
  switch (action.type) {
    case UserAction.CREATION_START:
      return getEmptyUser();
    case UserAction.ADD_USER:
      return {
        id: action.id,
        name: action.name,
        authentication_identity: action.authentication_identity
      };
    case UserAction.ADD_USERS:
      return action.user.map(u => u);
    default:
      return state;
  }
};

const getProjectsReducer = (state = [], action) => {
  const copy = state.slice();
  switch (action.type) {
    case UserAction.CREATION_START:
      return [user(undefined, action), ...state];
    case UserAction.CREATION_END:
      copy.shift();
      return [...copy];
    case UserAction.ADD_USER:
      return [...state, user(undefined, action)].sort(compareUsersByFirstName);
    case UserAction.ADD_USERS:
      return [...state, ...user(undefined, action)].sort(
        compareUsersByFirstName
      );
    case UserAction.UPDATE:
      for (let i = 0; i < copy.length; i++) {
        if (copy[i].id === action.user.id) {
          copy[i] = action.user;
          break;
        }
      }
      return [...copy].sort(compareUsersByFirstName);
    case UserAction.REMOVE:
      for (let i = 0; i < copy.length; i++) {
        if (copy[i].id === action.user.id) {
          copy.splice(i, 1);
          break;
        }
      }
      return [...copy];
    default:
      return state;
  }
};

// If you want your app to work offline and load faster, you can change
// unregister() to register() below. Note this comes with some pitfalls.
// Learn more about service workers: http://bit.ly/CRA-PWA
serviceWorker.unregister();
